import React from "react";
import ButtonChallenge1aa from "./buttonchallenge1aa";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import styled from "styled-components";
const Styles = styled.div`
  .navbar {
    background-color: #222;
  }
  a,
  .navbar-nav,
  .navbar-light .nav-link {
    color: #9fffcb;
    &:hover {
      color: white;
    }
  }
  .navbar-brand {
    font-size: 1.4em;
    color: #9fffcb;
    &:hover {
      color: white;
    }
  }
  .form-center {
    position: absolute !important;
    left: 25%;
    right: 25%;
  }
`;
export const NavigationBar = () => (
  <Styles>
    <Navbar expand="lg">
      <Navbar.Brand href="/">Tutorial</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Form className="form-center">
        <FormControl type="text" placeholder="Search" className="" />
      </Form>
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="ml-auto">
          <Nav.Item>
            <Nav.Link href="/">Home</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link href="/about">About</Nav.Link>
          </Nav.Item>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  </Styles>
);
//nav bar ends

class ButtonChallenge1aaa extends React.Component {
  constructor() {
    super();
    this.state = {
      p00: "-",
      p01: "-",
      p02: "-",
      p10: "-",
      p11: "-",
      p12: "-",
      p20: "-",
      p21: "-",
      p22: "-",
      player: null,
      winnerPlayer: null,
    };
  }

  myClickHandler = (event) => {
    let nam = event.target.name;
    let val = this.state[nam];
    //alert("Button color is: " + nam);
    // alert("Button value is: " + val);

    if (val == "-") {
      val = "X";
    } else if (val == "X") {
      val = "O";
    } else if (val == "O") {
      val = "X";
    }
    this.setState({ [nam]: val });
    if (this.state.p00 === this.state.p01 && this.state.p01 === this.state.p02)
      alert(this.state.p00 + this.state.p01 + this.state.p02);
  };

  render() {
    return (
      <div>
        <h1>Hi, I am ButtonChallenge component.</h1>
        <div>
          <button
            disabled={this.state.p00 != "-"}
            type="button"
            onClick={this.myClickHandler}
            name="p00"
            style={{
              backgroundColor: "red",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            {this.state.p00}
          </button>
          <button
            disabled={this.state.p01 != "-"}
            type="button"
            onClick={this.myClickHandler}
            name="p01"
            style={{
              backgroundColor: "blue",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            {this.state.p01}
          </button>

          <button
            disabled={this.state.p02 != "-"}
            type="button"
            onClick={this.myClickHandler}
            name="p02"
            style={{
              backgroundColor: "green",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            {this.state.p02}
          </button>
        </div>
        <div>
          <button
            disabled={this.state.p10 != "-"}
            type="button"
            onClick={this.myClickHandler}
            name="p10"
            style={{
              backgroundColor: "teal",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            {this.state.p10}
          </button>
          <button
            disabled={this.state.p11 != "-"}
            type="button"
            onClick={this.myClickHandler}
            name="p11"
            style={{
              backgroundColor: "orange",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            {this.state.p11}
          </button>
          <button
            disabled={this.state.p12 != "-"}
            type="button"
            onClick={this.myClickHandler}
            name="p12"
            style={{
              backgroundColor: "purple",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            {this.state.p12}
          </button>
        </div>
        <div>
          <button
            disabled={this.state.p20 != "-"}
            type="button"
            onClick={this.myClickHandler}
            name="p20"
            style={{
              backgroundColor: "yellow",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            {this.state.p20}
          </button>
          <button
            disabled={this.state.p21 != "-"}
            type="button"
            onClick={this.myClickHandler}
            name="p21"
            style={{
              backgroundColor: "skyblue",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            {this.state.p21}
          </button>
          <button
            disabled={this.state.p22 != "-"}
            type="button"
            onClick={this.myClickHandler}
            name="p22"
            style={{
              backgroundColor: "brown",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            {this.state.p22}
          </button>
        </div>
      </div>
    );
  }
}

export default ButtonChallenge1aaa;
