import React from "react";

class TableParameter extends React.Component {
  constructor() {
    super();
    this.state = { a: null, b: null, c: null, tabledata: "" };
  }

  sum = () => {
    var a = this.state.a,
      b = this.state.b,
      c = this.state.c;
    c = a + b;
    this.setState({ c: c });
  };

  tableparameter = () => {
    var i1 = this.state.a,
      i2 = this.state.b,
      tabledata = "";

    for (let i = i1; i <= i2; i++) {
      tabledata += "-";
      tabledata += " <br /> ";
      for (let j = 1; j <= 10; j++) {
        if ((j * i) % 2 === 1) tabledata += "(Odd)";
        else tabledata += "(Even)";
        tabledata += i * j;
        tabledata += "      ";
      }
    }

    this.setState({ tabledata: tabledata });
  };

  render() {
    return (
      <>
        <form>
          <h1>
            Your numbers {this.state.username} {this.state.age}
          </h1>

          <p>Enter your age:</p>
          <input type="text" name="a" onChange={this.myChangeHandler} />
          {this.state.errormessage}
          <input type="text" name="b" onChange={this.myChangeHandler} />
          {this.state.errormessage}
        </form>
        <div>
          <h1>Hi, I am TableParameter component .</h1>
          <h2>
            a[{this.state.a}] + b[{this.state.b}] = c [{this.state.c}]
          </h2>
          <button type="button" onClick={this.sum}>
            a + b
          </button>

          <button type="button" onClick={this.tableparameter}>
            Print Table
          </button>
          <div>
            Without HTML Tags:
            <br /> {this.state.tabledata} <br /> With HTML Tags:
          </div>
          <div dangerouslySetInnerHTML={{ __html: this.state.tabledata }} />
        </div>
      </>
    );
  }
}

export default TableParameter;
