import React, { Component } from "react";
//import ReactDOM from "react-dom";
import logo from "./logo.svg";
//import "./App.css";
import "./tictac.css";
import TableParameter2 from "./tablechallenge2a.js";
import Football from "./Buttons9";
import Main from "./Main";
import ButtonChallenge1 from "./buttonchallenge1";
import MenuNav1 from "./menunav1";

class App extends Component {
  constructor() {
    super();
    this.state = {
      board: Array(9).fill(null),
    };
  }
  handleClick(index) {
    let newBoard = this.state.board;
    newBoard[index] = "X";
    this.setState({
      board: newBoard,
    });
    console.log(this.state.board);
    //consol.log(index)
  }
  myClickHandler = (event) => {
    let nam = event.target.name;
    let val = this.state[nam];

    //alert("Button position is: " + nam);
    if (val == "-") {
      val = "X";
    } else if (val == "X") {
      val = "O";
    } else if (val == "O") {
      val = "X";
    } else {
      alert(
        "Program has encountered some error, please check if else statements."
      );
    }
    this.setState({ [nam]: val });
  };
  renderBoxes() {
    return this.state.board.map((box, index) => (
      <div className="box" key={index} onClick={() => this.handleClick(index)}>
        {box}{" "}
      </div>
    ));
  }
  render() {
    return (
      <>
        <div className="container">
          <h1>9 button box</h1>

          <div className="board">{this.renderBoxes()}</div>
        </div>
      </>
    );
  }
}

export default App;
