import React, { Component } from "react";

class App extends React.Component {
  state = {
    initialValue: 0,
    finalValue: initialValue * 10,
  };
  render() {
    return (
      <div>
        <input
          type="number"
          value={this.state.initialValue}
          onChange={this.alpha}
          name="initialValue"
          id="initialValue"
        />
        <span>{this.state.finalValue}</span>
      </div>
    );
  }
  alpha = (ev) => {
    this.setState({
      [ev.target.name]: ev.target.value,
    });
  };
}
export default App;
