import React from "react";

class Buttons9aa extends React.Component {
  constructor(props) {
    super(props);
    this.state = { color: "x" };
  }

  changeColor = () => {
    this.setState({ color: "o" });
  };
  myChangeHandler = () => {
    this.setState({ color: "x" });
  };
  render() {
    return (
      <div>
        <button
          type="button"
          style={{
            backgroundColor: "red",
            width: "250px",
            height: "160px",
            margin: "20px",
          }}
          if
          onClick={this.changeColor}
          else
          onclick={this.myChangeHandler}
        >
          {this.state.color}
        </button>
      </div>
    );
  }
}

export default Buttons9aa;
