import React from "react";

class ButtonChallenge1a extends React.Component {
  constructor() {
    super();
  }

  myClickHandler = (event) => {
    let nam = event.target.name;
    alert("Button color is: " + nam);
  };

  render() {
    return (
      <div>
        <h1>Hi, I am ButtonChallenge component.</h1>
        <div>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="red"
            style={{
              backgroundColor: "red",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P00
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="blue"
            style={{
              backgroundColor: "blue",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P01
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="green"
            style={{
              backgroundColor: "green",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P02
          </button>
        </div>
        <div>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="teal"
            style={{
              backgroundColor: "teal",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P10
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="orange"
            style={{
              backgroundColor: "orange",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P11
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="purple"
            style={{
              backgroundColor: "purple",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P12
          </button>
        </div>
        <div>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="yellow"
            style={{
              backgroundColor: "yellow",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P20
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="skyblue"
            style={{
              backgroundColor: "skyblue",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P21
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="brown"
            style={{
              backgroundColor: "brown",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P22
          </button>
        </div>
      </div>
    );
  }
}

export default ButtonChallenge1a;
