import React from "react";

class ButtonChallenge1a extends React.Component {
  constructor() {
    super();
    this.state = {
      P00: "-",
      P01: "-",
      P02: "-",
      P10: "-",
      P11: "-",
      P12: "-",
      P20: "-",
      P21: "-",
      P22: "-",
    };
  }

  myClickHandler = (event) => {
    let nam = event.target.name;
    alert("Button color is: " + nam);
  };

  render() {
    return (
      <div>
        <h1>Hi, I am ButtonChallenge component.</h1>
        <div>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="00"
            style={{
              backgroundColor: "red",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P00
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="P01"
            style={{
              backgroundColor: "blue",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P01
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="P02"
            style={{
              backgroundColor: "green",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P02
          </button>
        </div>
        <div>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="P10"
            style={{
              backgroundColor: "teal",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P10
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="P11"
            style={{
              backgroundColor: "orange",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P11
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="P12"
            style={{
              backgroundColor: "purple",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P12
          </button>
        </div>
        <div>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="P20"
            style={{
              backgroundColor: "yellow",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P20
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="P21"
            style={{
              backgroundColor: "skyblue",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P21
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="P22"
            style={{
              backgroundColor: "brown",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            P22
          </button>
        </div>
      </div>
    );
  }
}

export default ButtonChallenge1a;
