class Buttons9a extends React.Component {
  constructor(props) {
    super(props);
    this.state = { text: "" };
  }
  shouldComponentUpdate() {
    return true;
  }
  changeColor = () => {
    this.setState({ color: " color red" });
  };
  render() {
    return (
      <div>
        <button
          type="button"
          style={{ backgroundColor: "red", height: "20px", width: "100px" }}
          onClick={this.changeColor}
        >
          {" "}
          {this.state.color}
        </button>
      </div>
    );
  }
}

//---------
export default Buttons9a;
