import React, { Component } from "react";
//import ReactDOM from "react-dom";
import logo from "./logo.svg";
//import "./App.css";
import "./tictac.css";
import TableParameter2 from "./tablechallenge2a.js";
import Football from "./Buttons9";
import Main from "./Main";
import ButtonChallenge1 from "./buttonchallenge1";
import MenuNav1 from "./menunav1";

class App extends Component {
  constructor() {
    super();
    this.state = {
      board: Array(9).fill(null),
    };
  }
  handleClick(index) {
    let newBoard = this.state.board;
    newBoard[index] = "X";
    this.setState({
      board: newBoard,
    });
    console.log(this.state.board);
    //consol.log(index)
  }
  renderBoxes() {
    return this.state.board.map((box, index) => (
      <div className="box" key={index} onClick={() => this.handleClick(index)}>
        {box}{" "}
      </div>
    ));
  }
  render() {
    return (
      <>
        <div className="container">
          <h1>9 button box</h1>

          <div className="board">{this.renderBoxes()}</div>
          <button disabled={!this.state.winner} onClick={() => this.reset()}>
            {" "}
            Reset
          </button>
        </div>
      </>
    );
  }
}

export default App;
