import React, { Component } from "react";
import "./tictac.css";

class Tictac extends Component {
  constructor() {
    super();
    this.state = {
      board: Array(9).fill(null),
      player: null,
    };
  }
  handleClick(index) {
    if (this.state.player && !this.state.winner) {
      let newBoard = this.state.board;
      if (this.state.board[index] === null) {
        newBoard[index] = this.state.player;
        this.setState({
          board: newBoard,
          player: this.state.player === "X" ? "O" : "X",
        });
        this.checkWinner();
      }
    }
  }
  /*handleClick(index) {
    let newBoard = this.state.board;

    newBoard[index] = this.state.player ? "x" : "o";

    this.setState({
      board: newBoard,
    });
    console.log(this.state.board);
    //consol.log(index)
  }*/
  setPlayer(player) {
    this.setState({ player });
  }

  renderBoxes() {
    return this.state.board.map((box, index) => (
      <div className="box" key={index} onClick={() => this.handleClick(index)}>
        {box}{" "}
      </div>
    ));
  }
  render() {
    return (
      <>
        <div className="container">
          <h1>9 button box</h1>

          <div className="board">{this.renderBoxes()}</div>
        </div>
      </>
    );
  }
}

export default Tictac;
