import React from "react";

const Game = {
  display: "flex",
  flexDirection: "row",
  marginLeft: "40%",
  marginTop: "10%",
};

const squareGame = {
  background: "#a93f5c",
  border: "1px solid black",
  float: "left",
  fontSize: "50px",
  fontWeight: "bold",
  lineHeight: "34px",
  height: "90px",
  margin: "4px",
  padding: "10px",
  textAlign: "center",
  width: "90px",
  color: "#fff",
};

function Square(props) {
  return (
    <button style={squareGame} onClick={props.onClick}>
      {props.value}
    </button>
  );
}
//Class Status

class Status extends Component {
  handleSetPlayer(e) {
    this.props.setPlayer(e);
  }
  renderHtml() {
    if (this.props.winner) {
      return <h2>Winner is {this.props.winner}</h2>;
    } else {
      return this.props.player ? (
        <h2>Next player is {this.props.player}</h2>
      ) : (
        <Player player={(e) => this.handleSetPlayer(e)} />
      );
    }
  }
  render() {
    return <span>{this.renderHtml()}</span>;
  }
}

//class board
class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill(null),
      xIsNext: null,
      player1: "",
      player2: "",
    };
  }
  //check winner

  checkWinner() {
    let winLines = [
      ["0", "1", "2"],
      ["3", "4", "5"],
      ["6", "7", "8"],
      ["0", "3", "6"],
      ["1", "4", "7"],
      ["2", "5", "8"],
      ["0", "4", "8"],
      ["2", "4", "6"],
    ];
    this.checkMatch(winLines);
  }

  checkMatch(winLines) {
    for (let index = 0; index < winLines.length; index++) {
      const [a, b, c] = winLines[index];
      let board = this.state.board;
      if (board[a] && board[a] === board[b] && board[a] === board[c]) {
        alert("You won");
        this.setState({
          winner: this.state.player,
        });
      }
    }
  }

  // handleclick

  handleClick(i) {
    const squares = this.state.squares.slice();
    if (calculater(squares) || squares[i]) {
      return;
    }

    squares[i] = this.state.xIsNext ? "x" : "o";
    this.setState({
      squares: squares,
      xIsNext: !this.state.xIsNext,
    });
  }

  rendersquare(i) {
    return (
      <Square
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }
  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });
  };

  render() {
    const status =
      "Next Player is " +
      (this.state.xIsNext
        ? this.state.player1 + "  X"
        : this.state.player2 + "  O");
    return (
      <>
        <form>
          <p>Player1: {this.state.player1}</p>
          <input type="text" name="player1" onChange={this.myChangeHandler} />

          <p>Player2: {this.state.player2}</p>
          <input type="text" name="player2" onChange={this.myChangeHandler} />
        </form>
        <div>{status}</div>
        <div className="border-row">
          {this.rendersquare(0)}
          {this.rendersquare(1)}
          {this.rendersquare(2)}
        </div>
        <div className="border-row">
          {this.rendersquare(3)}
          {this.rendersquare(4)}
          {this.rendersquare(5)}
        </div>
        <div className="border-row">
          {this.rendersquare(6)}
          {this.rendersquare(7)}
          {this.rendersquare(8)}
        </div>
      </>
    );
  }
}

class Box extends React.Component {
  render() {
    return (
      <>
        <div style={Game}>
          <div>
            <Board />
          </div>
        </div>
      </>
    );
  }
}

function calculater() {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    return null;
  }
}

export default Box;
