import React from "react";

class ButtonChallenge1a extends React.Component {
  constructor() {
    super();
    this.state = {
      p00: "-",
      p01: "-",
      p02: "-",
      p10: "-",
      p11: "-",
      p12: "-",
      p20: "-",
      p21: "-",
      p22: "-",
    };
  }

  myClickHandler = (event) => {
    let nam = event.target.name;
    alert("Button color is: " + nam);
  };

  render() {
    return (
      <div>
        <h1>Hi, I am ButtonChallenge component.</h1>
        <div>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="p00"
            style={{
              backgroundColor: "red",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            p00
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="p01"
            style={{
              backgroundColor: "blue",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            p01
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="p02"
            style={{
              backgroundColor: "green",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            p02
          </button>
        </div>
        <div>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="p10"
            style={{
              backgroundColor: "teal",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            p10
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="p11"
            style={{
              backgroundColor: "orange",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            p11
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="p12"
            style={{
              backgroundColor: "purple",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            p12
          </button>
        </div>
        <div>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="p20"
            style={{
              backgroundColor: "yellow",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            p20
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="p21"
            style={{
              backgroundColor: "skyblue",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            p21
          </button>
          <button
            type="button"
            onClick={this.myClickHandler}
            name="p22"
            style={{
              backgroundColor: "brown",
              width: "150px",
              height: "60px",
              margin: "20px",
            }}
          >
            p22
          </button>
        </div>
      </div>
    );
  }
}

export default ButtonChallenge1a;
