import React from "react";

class TableParameter extends React.Component {
  constructor() {
    super();
    this.state = { a: 4, b: 5, c: null, tabledata: "" };
  }

  sum = () => {
    var a = this.state.a,
      b = this.state.b,
      c = this.state.c;
    c = a + b;
    this.setState({ c: c });
  };

  tableparameter = () => {
    var i1 = this.state.a,
      i2 = this.state.b,
      tabledata = "";

    for (let i = i1; i <= i2; i++) {
      tabledata += "-";
      tabledata += " <br /> ";
      for (let j = 1; j <= 10; j++) {
        if ((j * i) % 2 === 1) tabledata += "(Odd)";
        else tabledata += "(Even)";
        tabledata += i * j;
        tabledata += "      ";
      }
    }

    this.setState({ tabledata: tabledata });
  };
  /*class MyForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = { username: '' };
    }*/
  mySubmitHandler = (event) => {
    event.preventDefault();
    alert("You are submitting " + this.state.username);
  };
  /* myChangeHandler = (event) => {
    this.setState({ username: event.target.value });
  };*/
  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });
  };
  render() {
    return (
      <>
        <form>
          <h1>
            Hello {this.state.username} {this.state.age}
          </h1>
          <p>Enter your name:</p>
          <input type="text" name="username" onChange={this.myChangeHandler} />
          <p>Enter your age:</p>
          <input type="text" name="age" onChange={this.myChangeHandler} />
        </form>
        <div>
          <h1>Hi, I am TableParameter component testing .</h1>
          <h2>
            a[{this.state.a}] + b[{this.state.b}] = c [{this.state.c}]
          </h2>
          <button type="button" onClick={this.sum}>
            a + b
          </button>

          <button type="button" onClick={this.tableparameter}>
            Print Table
          </button>
          <div>
            Without HTML Tags:
            <br /> {this.state.tabledata} <br /> With HTML Tags:
          </div>
          <div dangerouslySetInnerHTML={{ __html: this.state.tabledata }} />
        </div>
      </>
    );
  }
}

export default TableParameter;
