import React from "react";

class Buttons9aaa extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      color: "Click Me",
      color2: "Click Me",
    };
  }

  changeColor = () => {
    let color = this.state.color;
    let color2 = this.state.color2;
    if (color == "Click Me") {
      color = "I am red";
    } else if (color == "I am red") {
      color = "you clicked again I am still red";
    } else if (color == "you clicked again I am still red") {
      color = "You are stupid clicking me again and again please stop";
    } else if (
      color == "You are stupid clicking me again and again please stop"
    ) {
      color = "एक बार और क्लिक किया तो देखलो कूट दूंगा ";
    } else if (color == "एक बार और क्लिक किया तो देखलो कूट दूंगा ") {
      color = "तुम तो निरे मूरख हो कंप्यूटर भी कभी कुटाई करते हैं क्या?";
    }
    if (color2 == "Click Me") {
      color2 = "I am blue";
    } else if (color2 == "I am Blue") {
      color2 = "you clicked again";
    }
    this.setState({ color: color, color2: color2 });
  };

  render() {
    return (
      <div>
        <button
          type="button"
          style={{
            backgroundColor: "red",
            width: "250px",
            height: "160px",
            margin: "20px",
          }}
          onClick={this.changeColor}
        >
          {this.state.color}
        </button>
        <button
          type="button"
          style={{
            backgroundColor: "blue",
            width: "250px",
            height: "160px",
            margin: "20px",
          }}
          onClick={this.changeColor}
        >
          {this.state.color2}
        </button>
      </div>
    );
  }
}

export default Buttons9aaa;
